// DOM Elements
const courseGrid = document.getElementById('course-grid');
const filterBtns = document.querySelectorAll('.filter-btn');
const faqItems = document.querySelectorAll('.faq-item');
const navbar = document.querySelector('.navbar');
const hamburger = document.querySelector('.hamburger');
const navLinks = document.querySelector('.nav-links');

// Render Courses
function renderCourses(filter = 'all') {
  courseGrid.innerHTML = '';

  const filteredCourses = filter === 'all'
    ? courses
    : courses.filter(course => course.category === filter);

  filteredCourses.forEach((course, index) => {
    const card = document.createElement('div');
    card.className = 'course-card reveal';
    card.style.transitionDelay = `${(index % 4) * 0.1}s`; // Staggered delay

    card.innerHTML = `
      <div class="course-image">
        <img src="${course.image}" alt="${course.title}" loading="lazy">
        <span class="course-tag">${course.category.toUpperCase()}</span>
      </div>
      <div class="course-content">
        <h3 class="course-title">${course.title}</h3>
        <div class="course-meta">
          <span><i class="fa-regular fa-clock"></i> ${course.duration}</span>
          <span><i class="fa-solid fa-building-columns"></i> ${course.institute}</span>
        </div>
        <a href="${course.link}" target="_blank" class="course-link">View Details <i class="fa-solid fa-arrow-right"></i></a>
      </div>
    `;

    // Add click event to the whole card for better UX, but respect the link
    card.addEventListener('click', (e) => {
      // If the user clicked the link directly, let it handle the navigation
      if (e.target.closest('.course-link')) return;

      // Otherwise, open the link
      window.open(course.link, '_blank');
    });

    courseGrid.appendChild(card);
  });

  // Re-observe new elements
  observeElements();
}

// Filter Functionality
filterBtns.forEach(btn => {
  btn.addEventListener('click', () => {
    // Remove active class from all
    filterBtns.forEach(b => b.classList.remove('active'));
    // Add active class to clicked
    btn.classList.add('active');

    const filter = btn.getAttribute('data-filter');
    renderCourses(filter);
  });
});

// FAQ Accordion
faqItems.forEach(item => {
  const question = item.querySelector('.faq-question');

  question.addEventListener('click', () => {
    const isActive = item.classList.contains('active');

    // Close all others
    faqItems.forEach(i => i.classList.remove('active'));

    // Toggle current
    if (!isActive) {
      item.classList.add('active');
    }
  });
});

// Scroll Animation (Intersection Observer)
function observeElements() {
  const observerOptions = {
    threshold: 0.1,
    rootMargin: "0px 0px -50px 0px"
  };

  const observer = new IntersectionObserver((entries) => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        entry.target.classList.add('active');
        observer.unobserve(entry.target); // Only animate once
      }
    });
  }, observerOptions);

  document.querySelectorAll('.reveal').forEach(el => {
    observer.observe(el);
  });
}

// Navbar Scroll Effect
window.addEventListener('scroll', () => {
  if (window.scrollY > 50) {
    navbar.classList.add('scrolled');
  } else {
    navbar.classList.remove('scrolled');
  }
});

// Mobile Menu
hamburger.addEventListener('click', () => {
  navLinks.style.display = navLinks.style.display === 'flex' ? 'none' : 'flex';
  if (navLinks.style.display === 'flex') {
    navLinks.style.flexDirection = 'column';
    navLinks.style.position = 'absolute';
    navLinks.style.top = '100%';
    navLinks.style.left = '0';
    navLinks.style.width = '100%';
    navLinks.style.background = 'rgba(10, 25, 47, 0.95)';
    navLinks.style.padding = '20px';
    navLinks.style.borderBottom = '1px solid var(--glass-border)';
  }
});

// Initial Render
document.addEventListener('DOMContentLoaded', () => {
  renderCourses();
  observeElements();
});
